import React, { useRef, useEffect } from 'react';
import '../styles/home.css';

function Home() {
  const carouselRef = useRef(null);
  const sectionRef = useRef(null);
  const reviews = [
    { text: '"Excelente servicio, entregaron a tiempo."', author: '- Juan, Dueño de Tienda' },
    { text: '"Muy profesionales, recomiendo."', author: '- María, Empresaria' },
    { text: '"Sitio web moderno y funcional."', author: '- Pedro, Startup Founder' },
    { text: '"Buena comunicación durante el proyecto."', author: '- Ana, Consultora' },
    { text: '"Precio justo por calidad alta."', author: '- Carlos, Gerente' }
  ];
  const duplicatedReviews = [...reviews, ...reviews, ...reviews]; // Duplicate more for smoother infinite loop

  useEffect(() => {
    const carousel = carouselRef.current;
    const section = sectionRef.current;
    if (!carousel || !section) return;

    let isDragging = false;
    let startX, scrollLeft;

    const startDrag = (e) => {
      isDragging = true;
      startX = e.pageX - carousel.offsetLeft;
      scrollLeft = carousel.scrollLeft;
      carousel.style.cursor = 'grabbing';
      carousel.style.animationPlayState = 'paused';
    };

    const drag = (e) => {
      if (!isDragging) return;
      e.preventDefault();
      const x = e.pageX - carousel.offsetLeft;
      const walk = (x - startX) * 2;
      carousel.scrollLeft = scrollLeft - walk;
    };

    const endDrag = () => {
      isDragging = false;
      carousel.style.cursor = 'grab';
      if (observer && isVisible) {
        carousel.style.animationPlayState = 'running';
      }
    };

    carousel.addEventListener('mousedown', startDrag);
    carousel.addEventListener('mousemove', drag);
    carousel.addEventListener('mouseup', endDrag);
    carousel.addEventListener('mouseleave', endDrag);

    let isVisible = false;
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          isVisible = entry.isIntersecting;
          carousel.style.animationPlayState = entry.isIntersecting ? 'running' : 'paused';
        });
      },
      { threshold: 0.5 } // Start when 50% visible
    );
    observer.observe(section);

    return () => {
      carousel.removeEventListener('mousedown', startDrag);
      carousel.removeEventListener('mousemove', drag);
      carousel.removeEventListener('mouseup', endDrag);
      carousel.removeEventListener('mouseleave', endDrag);
      observer.disconnect();
    };
  }, []);

  return (
    <div className="home">
      <section className="hero-section">
        <div className="hero-content">
          <img src="/llama-hero.png" alt="Llama Hero" className="llama-image" />
          <div className="text-container">
            <h1>Creación de Sitios Web en Argentina</h1>
            <p>Desarrollamos sitios web profesionales para tu negocio.</p>
          </div>
        </div>
      </section>

      <section className="services-section">
        <div className="services-content">
          <h2>Nuestros Servicios</h2>
          <div className="services-items">
            <div className="service-item">
              <h3>Página de Aterrizaje</h3>
              <p>Página simple y efectiva.</p>
              <p>Tiempo: 1 semana</p>
              <p>Precio desde: $500</p>
            </div>
            <div className="service-item">
              <h3>Sitio Web Empresarial</h3>
              <p>Sitio completo para empresas.</p>
              <p>Tiempo: 2-4 semanas</p>
              <p>Precio desde: $1000</p>
            </div>
            <div className="service-item">
              <h3>Sistema de Gestión de Contenidos</h3>
              <p>CMS para manejar tu contenido.</p>
              <p>Tiempo: 4-6 semanas</p>
              <p>Precio desde: $2000</p>
            </div>
          </div>
        </div>
      </section>

      <section className="advantages-section">
        <div className="advantages-content">
          <h2>Ventajas</h2>
          <ul>
            <li>
              <svg className="advantage-icon" width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M4 6H20M4 12H20M4 18H20" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              </svg>
              Diseños responsivos para todos los dispositivos.
            </li>
            <li>
              <svg className="advantage-icon" width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M21 21L15 15M17 10C17 13.866 13.866 17 10 17C6.13401 17 3 13.866 3 10C3 6.13401 6.13401 3 10 3C13.866 3 17 6.13401 17 10Z" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              </svg>
              Optimización SEO incluida.
            </li>
            <li>
              <svg className="advantage-icon" width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M3 12H21M3 6H21M3 18H21" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              </svg>
              Soporte post-lanzamiento.
            </li>
            <li>
              <svg className="advantage-icon" width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 2V22M5 5L19 19M5 19L19 5" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              </svg>
              Precios competitivos en el mercado argentino.
            </li>
            <li>
